# generate the software list
mvn dependency:list -DexcludeTransitive=true -DincludeScope=runtime -DexcludeGroupIds=com.connexta,ddf,org.codice  -pl '!distribution/docs,!distribution/docker/ddf-docker,!distribution/docker/solr-docker,!distribution/docker/solrcloud,!distribution/test/itests/test-itests-common,!distribution/test/itests/test-itests-ddf,!distribution/test/itests/test-itests-ddf-core,!distribution/test/itests/test-itests-kernel,!distribution/test/itests/test-itests-dependencies-app,!distribution/test/example-bundle,!distribution/test/features-xml,!distribution/test/performance,!distribution/test/test-input-transformer-stubs,!distribution/test/test-metacard-validator,!distribution/test/test-rest-endpoint,!distribution/test/test-storageplugins' | grep -i compile | sort | uniq -u | cut -d ":" -f 2,4 | sed s/:/-/g > ddf-deplist.out
